import * as React from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

export default function FormDialog(props: { setOpen: (arg0: boolean) => void, open: any; name: string; position: string, birthDate: string, salary: string, setEmployeesList: any }) {

    const [editValues, setEditValues] = React.useState();

    const handleClickOpen = () => {
        props.setOpen(true);
      };

      const handleClose = () => {
        props.setOpen(false);
      };

      const handleChangeValues = (value: { target: { id: any; value: any; }; }) => {
        setEditValues((prevValues: any) => ({
          ...prevValues,
          [value.target.id]: value.target.value
        }))
      }

  return (
      <Dialog open={props.open} onClose={handleClose}>
        <DialogTitle>Editar</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Nome"
            onChange={handleChangeValues}
            defaultValue={props.name}
            type="text"
            fullWidth
            variant="standard"
          />
          <TextField
            autoFocus
            margin="dense"
            id="position"
            label="Cargo"
            onChange={handleChangeValues}
            defaultValue={props.position}
            type="text"
            fullWidth
            variant="standard"
          />
          <TextField
            autoFocus
            margin="dense"
            id="birthDate"
            label="Data de aniversário"
            onChange={handleChangeValues}
            defaultValue={props.birthDate}
            type="text"
            fullWidth
            variant="standard"
          />
          <TextField
            autoFocus
            margin="dense"
            id="salary"
            label="Salário"
            onChange={handleChangeValues}
            defaultValue={props.salary}
            type="text"
            fullWidth
            variant="standard"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancelar</Button>
          <Button onClick={handleClose}>Remover</Button>
          <Button onClick={handleClose}>Salvar</Button>
        </DialogActions>
      </Dialog>
  );
}
