import React from "react";

type MyProps = {
   name : string,
   position : string,
   birthDate: string,
   salary: string,
   positionDesc: string,
  };

  class EmployeeCard extends React.Component<MyProps> {
    render() {
        const { name, position, birthDate, salary, positionDesc } = this.props
      return (
        <div className="col">
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title">{name}</h5>
                    <p className="card-text">{positionDesc}</p>
                    <ul className="list-group list-group-flush">
                        <li className="list-group-item">{position}</li>
                        <li className="list-group-item">{birthDate}</li>
                        <li className="list-group-item">{salary}</li>
                    </ul>
                </div>
            </div>
        </div>
    );
    }
  }
export default EmployeeCard;
