import React from "react";

  type MyProps = {
    onChange: any;
    handleClick: any,
    state: object,
  };

  class RegistrationForm extends React.Component<MyProps> {
    render() {
      return (
        <div className="container-sm mt-5">
          <div className="input-group mb-3">
            <input onChange={this.props.onChange} name="name" type="text" className="form-control" placeholder="Nome" />
            <input onChange={this.props.onChange}  name="lastName" type="text" className="form-control" placeholder="Sobrenome" />
          </div>

          <div className="input-group mb-3">
            <select onChange={this.props.onChange} name="select" className="form-select" id="inputGroupSelect01">
              <option defaultValue="true" >Selecione o cargo</option>
              <option value="1">Opção 1</option>
              <option value="2">Opção 2</option>
              <option value="3">Opção 3</option>
            </select>
          </div>

          <div className="input-group mb-3">
            <input onChange={this.props.onChange}  name="birthDate" type="text" className="form-control" placeholder="Data de Nascimento (xx/xx/xxxx)" />
          </div>

          <div className="input-group mb-3">
            <span className="input-group-text">R$</span>
            <input onChange={this.props.onChange}  name="salary" type="text" className="form-control" placeholder="Salário" />
            <span className="input-group-text">,00</span>
            <button onClick={this.props.handleClick} className="btn btn-outline-success" type="button">Adicionar Funcionário</button>
          </div>

        </div>
      );
    }
  }
export default RegistrationForm;
