import React, { useState } from "react";
import FormDialog from './dialog'

  function EmployeeCard (props: { name: string; position: string; birthDate: string; salary: string; positionDesc: string; }) {
      const [open, setOpen] = React.useState(true);
      const { name, position, birthDate, salary, positionDesc } = props

      return (
        <>
          <FormDialog open={open} setOpen={setOpen} />
          <div className="col">
              <div className="card">
                  <div className="card-body">
                      <h5 className="card-title">{name}</h5>
                      <p className="card-text">{positionDesc}</p>
                      <ul className="list-group list-group-flush">
                          <li className="list-group-item">{position}</li>
                          <li className="list-group-item">{birthDate}</li>
                          <li className="list-group-item">{salary}</li>
                      </ul>
                  </div>
              </div>
          </div>
        </>
    );

  }
export default EmployeeCard;
