import React from "react";

type MyProps = {
  handlePositionChange: any,
  positionState: any,
 };

  class DescriptionForm extends React.Component<MyProps> {
    render() {
      const { handlePositionChange, positionState } = this.props
      return (
        <div className="container-sm mt-5">
          <div className="input-group mb-3">
            <input onChange={handlePositionChange} name="positionName" type="text" className="form-control" placeholder="Nome do cargo" />
          </div>

          <div className="input-group mb-3">
            <textarea onChange={handlePositionChange} name="positionDesc" className="form-control" placeholder="Descrição do cargo"/>
            <button className="btn btn-outline-success" type="button">Adicionar Cargo</button>
          </div>
        </div>
      );
    }
  }
export default DescriptionForm;
