import React, { useState } from "react";
import { createFalse } from "typescript";
import FormDialog from './dialog'

  function EmployeeCard (props: { name: string; position: string; birthDate: string; salary: string; }) {
      const [open, setOpen] = React.useState(false);
      const { name, position, birthDate, salary } = props

      const handleClickCard = () => {
        setOpen(true)
      }

      return (
        <>
          <FormDialog open={open} setOpen={setOpen} />
          <div className="col" onClick={() => {
            handleClickCard()
          }}>
              <div className="card">
                  <div className="card-body">
                      <h5 className="card-title">{name}</h5>
                      <ul className="list-group list-group-flush">
                          <li className="list-group-item">{position}</li>
                          <li className="list-group-item">{birthDate}</li>
                          <li className="list-group-item">{salary}</li>
                      </ul>
                  </div>
              </div>
          </div>
        </>
    );

  }
export default EmployeeCard;
