import React, { useState, useEffect } from 'react';
import Employees from './pages/Employees';
import Positions from './pages/Positions';
import PageNotFound from './pages/PageNotFound';
import { Routes, Route } from 'react-router-dom';
import Axios from "axios";
import { ImportsNotUsedAsValues } from 'typescript';

type State = {
  name: string,
  lastName: string,
  birthDate: string,
  salary: string,
  select: string,
}

function App() {

    const [state, setState] = useState<State>({
      name: '',
      lastName: '',
      birthDate: '',
      salary: '',
      select: '',
    })

    // console.log(state)

    const [employeesList, setEmployeesList] = useState();

    console.log(employeesList)

    function handleChange(evt: { target: { value: any; name: any; }; }) {

      const value = evt.target.value;
      setState({
        ...state,
        [evt.target.name]: value
      });
    }

    function handleClickButton(this: any) {
      Axios.post("http://localhost:3001/register", {
        full_name: `${state.name} ${state.lastName}`,
        birth_date: state.birthDate,
        salary: state.salary,
        position: state.select
      }).then(() => {
        if (Array.isArray(employeesList)){
          const result = employeesList.push({
            full_name: `${state.name} ${state.lastName}`,
            birth_date: state.birthDate,
            salary: state.salary,
            position: state.select
          })
        }
        setEmployeesList()
      });
    };


    useEffect(() => {
      Axios.get("http://localhost:3001/getEmployees").then((response) => {
        setEmployeesList(response.data);
    });
    }, []);

  return (
    <div className="App">
      <Routes>
        <Route path='/' element={ <Employees state={state} employeesList={ employeesList } setEmployeesList={ setEmployeesList } handleClick={handleClickButton} onChange={ handleChange } /> } />
        <Route path='/cargos' element={ <Positions /> } />
        <Route path='*' element={ <PageNotFound /> } />
      </Routes>
    </div>
  );

}

export default App;
